ee:message.ee:set-payload:
%dw 2.0
output application/json
---
{
	id: vars.resourceId,
	(payload - "id" - "meta"),
	meta: {
		versionId: payload.meta.versionId + 1,
		lastUpdated: (now() >> "UTC")
	}
}

